let navButton=document.getElementById('nav-button'),navMenu=document.getElementById('nav-menu'),navMenuFrame=document.getElementById('nav-menu-frame');navButton.addEventListener('click',function(){navButton.classList.contains('is-active')?(navButton.classList.remove('is-active'),navMenu.classList.remove('is-active'),navMenuFrame.classList.remove('is-active')):(navButton.classList.add('is-active'),navMenu.classList.add('is-active'),navMenuFrame.classList.add('is-active'))})